import React, { useState } from 'react';
import axios from 'axios';

const TwitterUserData = () => {
  const [username, setUsername] = useState('');
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState('');

  const fetchTwitterUserData = async () => {
    setError('');
    setUserData(null);

    if (!username.trim()) {
      setError('Please enter a valid username.');
      return;
    }

    try {
      const BEARER_TOKEN = 'AAAAAAAAAAAAAAAAAAAAAKs1xAEAAAAAz3oxsONxDhA9sMNQg6lxvsEn%2FQY%3DGkC6JBBYFxT4SXGUeqZBkPFRli6giLUeDX9IFfNwR8t9nzjSGK'; // Replace with your token
      const url = `https://api.twitter.com/2/users/by/username/${username}?user.fields=created_at,description,profile_image_url,public_metrics`;

      const response = await axios.get(url, {
        headers: {
          Authorization: `Bearer ${BEARER_TOKEN}`,
        },
      });

      setUserData(response.data);
    } catch (err) {
      setError('Failed to fetch user data. Please ensure the username is correct.');
      console.error(err);
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: 'auto', textAlign: 'center' }}>
      <h1>Twitter User Data</h1>
      <input
        type="text"
        placeholder="Enter Twitter username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        style={{ padding: '10px', width: '80%', marginBottom: '10px' }}
      />
      <button onClick={fetchTwitterUserData} style={{ padding: '10px 20px', cursor: 'pointer' }}>
        Fetch Data
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {userData && (
        <div style={{ marginTop: '20px', textAlign: 'left' }}>
          <h2>{userData.data.name} (@{userData.data.username})</h2>
          <img
            src={userData.data.profile_image_url}
            alt="Profile"
            style={{ borderRadius: '50%', width: '100px' }}
          />
          <p><strong>Description:</strong> {userData.data.description}</p>
          <p><strong>Joined:</strong> {new Date(userData.data.created_at).toLocaleDateString()}</p>
          <p><strong>Followers:</strong> {userData.data.public_metrics.followers_count}</p>
          <p><strong>Following:</strong> {userData.data.public_metrics.following_count}</p>
          <p><strong>Tweets:</strong> {userData.data.public_metrics.tweet_count}</p>
        </div>
      )}
    </div>
  );
};

export default TwitterUserData;
