import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Grid, Card, CardContent, CardMedia, Typography, CircularProgress } from "@mui/material";

const API_KEY = "9e9af039ba0d423082027358ed6cc54e";
const API_URL = `https://newsapi.org/v2/top-headlines?country=us&apiKey=${API_KEY}`;

function App() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await axios.get(API_URL);
        setArticles(response.data.articles);
      } catch (error) {
        console.error("Error fetching the news: ", error);
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, []);

  if (loading) {
    return (
      <Container style={{ textAlign: "center", marginTop: "20px" }}>
        <CircularProgress />
      </Container>
    );
  }

  return (
    <Container>
      <Typography variant="h4" component="h1" gutterBottom style={{ margin: "20px 0" }}>
        Latest News
      </Typography>
      <Grid container spacing={4}>
        {articles.map((article, index) => (
          <Grid item key={index} xs={12} sm={6} md={4}>
            <Card>
              <CardMedia
                component="img"
                alt={article.title}
                height="140"
                image={article.urlToImage || "https://via.placeholder.com/140"}
              />
              <CardContent>
                <Typography variant="h6" component="div">
                  {article.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" style={{ margin: "10px 0" }}>
                  {article.description}
                </Typography>
                <Typography variant="body2" color="primary">
                  <a href={article.url} target="_blank" rel="noopener noreferrer">
                    Read more
                  </a>
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default App;
