import React, { useState } from 'react';
import axios from 'axios';

const Twitter = () => {
    const [username, setUsername] = useState('');
    const [userData, setUserData] = useState(null);

    const fetchTwitterData = async () => {
        try {
            const response = await axios.get(`/api/twitter/${username}`);
            setUserData(response.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    return (
        <div>
            <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Enter Twitter username"
            />
            <button onClick={fetchTwitterData}>Get User Data</button>
            {userData && (
                <div>
                    <h2>{userData.name}</h2>
                    <img src={userData.profile_image_url} alt="Profile" />
                    <p>{userData.description}</p>
                    <p>Followers: {userData.public_metrics.followers_count}</p>
                    <p>Joined: {new Date(userData.created_at).toDateString()}</p>
                </div>
            )}
        </div>
    );
};

export default Twitter;
